import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";
import { collection, query, where, getDocs, doc, setDoc } from "firebase/firestore";
import { useAuth } from "../AuthContext";

function Places() {
  const { cityId } = useParams();
  const [places, setPlaces] = useState([]);
  const { user } = useAuth();

  // ุฌูุจ ุงูุฃูุงูู ุงูุฎุงุตุฉ ุจุงููุฏููุฉ
  useEffect(() => {
    const fetchPlaces = async () => {
      const q = query(collection(db, "places"), where("cityId", "==", cityId));
      const snapshot = await getDocs(q);
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setPlaces(data);
    };

    fetchPlaces();
  }, [cityId]);

  // ุฏุงูุฉ ุงูุญูุธ ูู ุงูููุถูุฉ
  const saveToFavorites = async (place) => {
    if (!user) {
      alert("ูุฌุจ ุชุณุฌูู ุงูุฏุฎูู ูุญูุธ ุงูููุงู");
      return;
    }

    try {
      const favRef = doc(db, "favorites", user.uid, "places", place.id);
      await setDoc(favRef, place);
      alert("โ ุชู ุญูุธ ุงูููุงู ูู ุงูููุถูุฉ");
    } catch (error) {
      console.error("โ ุฎุทุฃ ูู ุงูุญูุธ:", error);
      alert("ุญุฏุซ ุฎุทุฃ ุฃุซูุงุก ุงูุญูุธ ูู ุงูููุถูุฉ");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>๐บ๏ธ ุงูุฃูุงูู ุงูุณูุงุญูุฉ ูู ูุฐู ุงููุฏููุฉ</h2>
      {places.length === 0 ? (
        <p>ูุง ุชูุฌุฏ ุฃูุงูู ูุณุฌูุฉ ุจุนุฏ.</p>
      ) : (
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "15px"
        }}>
          {places.map(place => (
            <div key={place.id} style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              overflow: "hidden",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
            }}>
              <img src={place.imageUrl} alt={place.name} style={{ width: "100%", height: "200px", objectFit: "cover" }} />
              <div style={{ padding: "10px" }}>
                <h3>{place.name}</h3>
                <p>{place.description}</p>

                <button
                  onClick={() => saveToFavorites(place)}
                  style={{
                    marginTop: "10px",
                    backgroundColor: "#ff4081",
                    color: "white",
                    border: "none",
                    padding: "8px",
                    cursor: "pointer",
                    width: "100%"
                  }}
                >
                  โค๏ธ ุญูุธ ูู ุงูููุถูุฉ
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Places;
