import React, { useState, useEffect } from "react";
import { db } from "../firebase";
import { collection, addDoc, getDocs } from "firebase/firestore";

function AddPlace() {
  const [place, setPlace] = useState({
    name: "",
    description: "",
    imageUrl: "",
    cityId: ""
  });

  const [cities, setCities] = useState([]);

  // جلب المدن من Firestore
  useEffect(() => {
    const fetchCities = async () => {
      const snapshot = await getDocs(collection(db, "cities"));
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCities(data);
    };
    fetchCities();
  }, []);

  // عند إدخال البيانات
  const handleChange = (e) => {
    setPlace({ ...place, [e.target.name]: e.target.value });
  };

  // عند الضغط على "إرسال"
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, "places"), place);
      alert("✅ تم إضافة المكان بنجاح!");
      setPlace({ name: "", description: "", imageUrl: "", cityId: "" });
    } catch (err) {
      alert("حدث خطأ أثناء الإضافة");
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>➕ إضافة مكان سياحي جديد</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "400px" }}>
        <input type="text" name="name" placeholder="اسم المكان" value={place.name} onChange={handleChange} required />
        <textarea name="description" placeholder="وصف المكان" value={place.description} onChange={handleChange} required />
        <input type="text" name="imageUrl" placeholder="رابط الصورة" value={place.imageUrl} onChange={handleChange} required />

        <select name="cityId" value={place.cityId} onChange={handleChange} required>
          <option value="">اختر المدينة</option>
          {cities.map(city => (
            <option key={city.id} value={city.id}>{city.name}</option>
          ))}
        </select>

        <button type="submit" style={{ padding: "10px", backgroundColor: "#0077b6", color: "white", border: "none" }}>
          إضافة
        </button>
      </form>
    </div>
  );
}

export default AddPlace;
