import React, { useEffect, useState } from "react";
import { useAuth } from "../AuthContext";
import { db } from "../firebase";
import { collection, getDocs } from "firebase/firestore";

function Favorites() {
  const { user } = useAuth();
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    const fetchFavorites = async () => {
      if (!user) return;
      const favs = await getDocs(collection(db, "favorites", user.uid, "places"));
      const data = favs.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setPlaces(data);
    };

    fetchFavorites();
  }, [user]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>❤️ الأماكن المفضلة</h2>
      {places.length === 0 ? <p>لا توجد أماكن محفوظة.</p> : (
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", gap: "15px" }}>
          {places.map(place => (
            <div key={place.id} style={{
              border: "1px solid #ccc", borderRadius: "8px",
              overflow: "hidden", boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
            }}>
              <img src={place.imageUrl} alt={place.name} style={{ width: "100%", height: "200px", objectFit: "cover" }} />
              <div style={{ padding: "10px" }}>
                <h3>{place.name}</h3>
                <p>{place.description}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Favorites;
