import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

// الصفحات
import Cities from "./pages/Cities";
import Places from "./pages/Places";
import Login from "./pages/Login";
import AddPlace from "./pages/AddPlace";
import Favorites from "./pages/Favorites";
import Profile from "./pages/Profile";

import "./App.css";

// السياق للمصادقة
import { AuthProvider, useAuth } from "./pages/AuthContext";
import ProtectedRoute from "./ProtectedRoute";
import { auth } from "./firebase";
import { signOut } from "firebase/auth";

function Navbar() {
  const { user } = useAuth();

  const handleLogout = () => {
    signOut(auth);
  };

  return (
    <nav className="navbar">
      <h2>📍 دليلك السياحي</h2>
      <ul>
        <li><Link to="/">المدن</Link></li>
        {user && <li><Link to="/add">إضافة مكان</Link></li>}
        {user && <li><Link to="/favorites">المفضلة</Link></li>}
        {user && <li><Link to="/profile">الملف الشخصي</Link></li>}
        {!user && <li><Link to="/login">تسجيل الدخول</Link></li>}
        {user && (
          <li>
            <button onClick={handleLogout} style={{ background: "none", border: "none", color: "white", cursor: "pointer" }}>
              تسجيل الخروج
            </button>
          </li>
        )}
      </ul>
    </nav>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <Navbar />

        <Routes>
          <Route path="/" element={<Cities />} />
          <Route path="/city/:cityId" element={<Places />} />
          <Route path="/login" element={<Login />} />

          {/* الصفحات المحمية */}
          <Route path="/add" element={
            <ProtectedRoute>
              <AddPlace />
            </ProtectedRoute>
          } />
          <Route path="/favorites" element={
            <ProtectedRoute>
              <Favorites />
            </ProtectedRoute>
          } />
          <Route path="/profile" element={
            <ProtectedRoute>
              <Profile />
            </ProtectedRoute>
          } />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
