import React, { useState } from "react";
import { auth } from "../firebase";
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css";

function Login() {
  const navigate = useNavigate();
  const [isRegister, setIsRegister] = useState(false);
  const [form, setForm] = useState({ email: "", password: "" });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isRegister) {
        await createUserWithEmailAndPassword(auth, form.email, form.password);
        alert("‚úÖ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿ®ŸÜÿ¨ÿßÿ≠");
      } else {
        await signInWithEmailAndPassword(auth, form.email, form.password);
        alert("‚úÖ ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ŸÜÿ¨ÿßÿ≠");
      }

      navigate("/");
    } catch (err) {
      alert("‚ùå ÿÆÿ∑ÿ£: " + err.message);
    }
  };

  return (
    <div className="login-page">
      <div className="login-form-container">
        <h2>{isRegister ? "ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ" : "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ"}</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"
            value={form.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
            value={form.password}
            onChange={handleChange}
            required
          />
          <button type="submit">{isRegister ? "ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ®" : "ÿØÿÆŸàŸÑ"}</button>
        </form>
        <button onClick={() => setIsRegister(!isRegister)} className="toggle-button">
          {isRegister ? "üîÅ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ" : "üÜï ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ®"}
        </button>
      </div>
    </div>
  );
}

export default Login;
