import React, { useState } from "react";
import { auth } from "../firebase";
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();
  const [isRegister, setIsRegister] = useState(false);
  const [form, setForm] = useState({ email: "", password: "" });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isRegister) {
        await createUserWithEmailAndPassword(auth, form.email, form.password);
        alert("تم إنشاء الحساب بنجاح ✅");
      } else {
        await signInWithEmailAndPassword(auth, form.email, form.password);
        alert("تم تسجيل الدخول بنجاح ✅");
      }

      navigate("/");
    } catch (err) {
      alert("❌ خطأ: " + err.message);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "auto" }}>
      <h2>{isRegister ? "إنشاء حساب جديد" : "تسجيل الدخول"}</h2>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <input
          type="email"
          name="email"
          placeholder="البريد الإلكتروني"
          value={form.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="كلمة المرور"
          value={form.password}
          onChange={handleChange}
          required
        />
        <button type="submit" style={{ padding: "10px", backgroundColor: "#0077b6", color: "white", border: "none" }}>
          {isRegister ? "إنشاء حساب" : "دخول"}
        </button>
      </form>
      <p style={{ marginTop: "10px" }}>
        {isRegister ? "لديك حساب؟" : "ليس لديك حساب؟"}{" "}
        <button onClick={() => setIsRegister(!isRegister)} style={{ color: "#0077b6", background: "none", border: "none", cursor: "pointer" }}>
          {isRegister ? "تسجيل الدخول" : "إنشاء حساب"}
        </button>
      </p>
    </div>
  );
}

export default Login;
