import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";
import { collection, query, where, getDocs, doc, setDoc } from "firebase/firestore";
import { useAuth } from "../AuthContext";

function Places() {
  const { cityId } = useParams(); // ‚Üê Ÿáÿ∞ÿß Ÿäÿπÿ∑ŸäŸÉ ÿßÿ≥ŸÖ ÿßŸÑŸÖÿØŸäŸÜÿ© ŸÖŸÜ ÿßŸÑÿ±ÿßÿ®ÿ∑
  const [places, setPlaces] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const { user } = useAuth();

  useEffect(() => {
    const fetchPlaces = async () => {
      const q = query(collection(db, "places"), where("cityId", "==", cityId));
      const snapshot = await getDocs(q);
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setPlaces(data);
    };

    fetchPlaces();
  }, [cityId]);

  const saveToFavorites = async (place) => {
    if (!user) {
      alert("Ÿäÿ¨ÿ® ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÑÿ≠ŸÅÿ∏ ÿßŸÑŸÖŸÉÿßŸÜ");
      return;
    }

    const favRef = doc(db, "favorites", user.uid, "places", place.id);
    await setDoc(favRef, place);
    alert("‚úÖ ÿ™ŸÖ ÿßŸÑÿ≠ŸÅÿ∏ ŸÅŸä ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©");
  };

  const filteredPlaces = places.filter(place =>
    place.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{ padding: "30px", fontFamily: "Tahoma, sans-serif", direction: "rtl" }}>
      <h2 style={{ marginBottom: "20px", color: "#023e8a" }}>üó∫Ô∏è ÿßŸÑÿ£ŸÖÿßŸÉŸÜ ÿßŸÑÿ≥Ÿäÿßÿ≠Ÿäÿ© ŸÅŸä: {cityId}</h2>

      <input
        type="text"
        placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÖŸÉÿßŸÜ..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{
          padding: "10px",
          fontSize: "16px",
          width: "100%",
          marginBottom: "25px",
          borderRadius: "6px",
          border: "1px solid #ccc"
        }}
      />

      {filteredPlaces.length === 0 ? (
        <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÖÿßŸÉŸÜ ŸÖÿ≥ÿ¨ŸÑÿ© ŸÅŸä Ÿáÿ∞Ÿá ÿßŸÑŸÖÿØŸäŸÜÿ©.</p>
      ) : (
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
          gap: "20px"
        }}>
          {filteredPlaces.map(place => (
            <div key={place.id} style={{
              border: "1px solid #ccc",
              borderRadius: "10px",
              overflow: "hidden",
              boxShadow: "0 3px 8px rgba(0,0,0,0.1)"
            }}>
              <img src={place.imageUrl} alt={place.name} style={{ width: "100%", height: "200px", objectFit: "cover" }} />
              <div style={{ padding: "15px" }}>
                <h3>{place.name}</h3>
                <p>{place.description}</p>
                <button
                  onClick={() => saveToFavorites(place)}
                  style={{
                    marginTop: "10px",
                    backgroundColor: "#ff4081",
                    color: "white",
                    border: "none",
                    padding: "10px",
                    cursor: "pointer",
                    width: "100%",
                    borderRadius: "6px"
                  }}
                >
                  ‚ù§Ô∏è ÿ≠ŸÅÿ∏ ŸÅŸä ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Places;
