import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";
import { collection, query, where, getDocs, doc, setDoc } from "firebase/firestore";
import { useAuth } from "../AuthContext";

function Places() {
  const { cityId } = useParams();
  const [places, setPlaces] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const { user } = useAuth();

  useEffect(() => {
    const fetchPlaces = async () => {
      const q = query(collection(db, "places"), where("cityId", "==", cityId));
      const snapshot = await getDocs(q);
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setPlaces(data);
    };

    fetchPlaces();
  }, [cityId]);

  const saveToFavorites = async (place) => {
    if (!user) {
      alert("يجب تسجيل الدخول لحفظ المكان");
      return;
    }

    try {
      const favRef = doc(db, "favorites", user.uid, "places", place.id);
      await setDoc(favRef, place);
      alert("✅ تم حفظ المكان في المفضلة");
    } catch (error) {
      console.error("❌ خطأ في الحفظ:", error);
      alert("حدث خطأ أثناء الحفظ في المفضلة");
    }
  };

  // 🔍 فلترة الأماكن حسب البحث
  const filteredPlaces = places.filter(place =>
    place.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{ padding: "20px" }}>
      <h2>🗺️ الأماكن السياحية في هذه المدينة</h2>

      {/* 🔍 شريط البحث */}
      <input
        type="text"
        placeholder="ابحث عن مكان..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{
          padding: "10px",
          fontSize: "16px",
          width: "100%",
          marginBottom: "20px",
          borderRadius: "6px",
          border: "1px solid #ccc"
        }}
      />

      {filteredPlaces.length === 0 ? (
        <p>لا توجد أماكن تطابق البحث.</p>
      ) : (
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "15px"
        }}>
          {filteredPlaces.map(place => (
            <div key={place.id} style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              overflow: "hidden",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
            }}>
              <img src={place.imageUrl} alt={place.name} style={{ width: "100%", height: "200px", objectFit: "cover" }} />
              <div style={{ padding: "10px" }}>
                <h3>{place.name}</h3>
                <p>{place.description}</p>

                <button
                  onClick={() => saveToFavorites(place)}
                  style={{
                    marginTop: "10px",
                    backgroundColor: "#ff4081",
                    color: "white",
                    border: "none",
                    padding: "8px",
                    cursor: "pointer",
                    width: "100%"
                  }}
                >
                  ❤️ حفظ في المفضلة
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Places;
