import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";
import { collection, query, where, getDocs, doc, setDoc } from "firebase/firestore";
import { useAuth } from "../AuthContext";

function Places() {
  const { cityId } = useParams();
  const [places, setPlaces] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const { user } = useAuth();

  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        const q = query(collection(db, "places"), where("cityId", "==", cityId));
        const snapshot = await getDocs(q);
        const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setPlaces(data);
      } catch (error) {
        console.error("خطأ أثناء جلب الأماكن:", error);
      }
    };

    fetchPlaces();
  }, [cityId]);

  const saveToFavorites = async (place) => {
    if (!user) {
      alert("يرجى تسجيل الدخول لحفظ في المفضلة.");
      return;
    }

    try {
      const favRef = doc(db, "favorites", user.uid, "places", place.id);
      await setDoc(favRef, place);
      alert("✅ تم الحفظ في المفضلة");
    } catch (err) {
      alert("❌ فشل في الحفظ");
      console.error(err);
    }
  };

  const filteredPlaces = places.filter(place =>
    place.name && place.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{ padding: "30px", fontFamily: "Tahoma, sans-serif", direction: "rtl" }}>
      <h2 style={{ marginBottom: "20px", color: "#023e8a" }}>🗺️ الأماكن السياحية في: {cityId}</h2>

      <input
        type="text"
        placeholder="🔍 ابحث عن مكان..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{
          padding: "10px",
          fontSize: "16px",
          width: "100%",
          marginBottom: "25px",
          borderRadius: "6px",
          border: "1px solid #ccc"
        }}
      />

      {filteredPlaces.length === 0 ? (
        <p style={{ color: "#555" }}>لا توجد أماكن تطابق البحث أو لم تُسجل بعد.</p>
      ) : (
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
          gap: "20px"
        }}>
          {filteredPlaces.map(place => (
            <div key={place.id} style={{
              borderRadius: "10px",
              overflow: "hidden",
              backgroundColor: "#fff",
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)"
            }}>
              <img
                src={place.imageUrl || "https://via.placeholder.com/400x200?text=No+Image"}
                alt={place.name || "مكان سياحي"}
                style={{ width: "100%", height: "200px", objectFit: "cover" }}
              />
              <div style={{ padding: "15px" }}>
                <h3 style={{ margin: "0 0 10px", color: "#0077b6" }}>{place.name || "بدون اسم"}</h3>
                <p style={{ color: "#444" }}>{place.description || "لا يوجد وصف."}</p>
                <button
                  onClick={() => saveToFavorites(place)}
                  style={{
                    marginTop: "10px",
                    backgroundColor: "#ff4081",
                    color: "white",
                    border: "none",
                    padding: "10px",
                    cursor: "pointer",
                    width: "100%",
                    borderRadius: "6px"
                  }}
                >
                  ❤️ حفظ في المفضلة
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Places;
