import React, { useState, useEffect } from "react";
import { db } from "../firebase";
import { collection, addDoc, getDocs } from "firebase/firestore";


function AddPlace() {
  const [place, setPlace] = useState({
    name: "",
    description: "",
    imageUrl: "",
    cityId: ""
  });

  const [cities, setCities] = useState([]);

  useEffect(() => {
    const fetchCities = async () => {
      const snapshot = await getDocs(collection(db, "cities"));
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCities(data);
    };
    fetchCities();
  }, []);

  const handleChange = (e) => {
    setPlace({ ...place, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, "places"), place);
      alert("✅ تم إضافة المكان بنجاح!");
      setPlace({ name: "", description: "", imageUrl: "", cityId: "" });
    } catch (err) {
      alert("حدث خطأ أثناء الإضافة");
      console.error(err);
    }
  };

  const backgroundStyle = {
    backgroundImage: 'url("/images/tourism-bg.jpg")',
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    minHeight: "100vh",
    padding: "30px",
    fontFamily: "Tahoma, sans-serif"
  };

  return (
    <div style={backgroundStyle}>
      <div className="add-place-container">
        <h2>➕ إضافة مكان سياحي جديد</h2>
        <form onSubmit={handleSubmit} className="add-place-form">
          <input
            type="text"
            name="name"
            placeholder="اسم المكان"
            value={place.name}
            onChange={handleChange}
            required
          />
          <textarea
            name="description"
            placeholder="وصف المكان"
            value={place.description}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="imageUrl"
            placeholder="رابط الصورة"
            value={place.imageUrl}
            onChange={handleChange}
            required
          />
          <select name="cityId" value={place.cityId} onChange={handleChange} required>
            <option value="">اختر المدينة</option>
            {cities.map(city => (
              <option key={city.id} value={city.id}>{city.name}</option>
            ))}
          </select>
          <button type="submit">إضافة</button>
        </form>
      </div>
    </div>
  );
}

export default AddPlace;
