import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";
import { collection, query, where, getDocs } from "firebase/firestore";
import { useAuth } from "../AuthContext";
import { doc, setDoc } from "firebase/firestore";


function Places() {
  const { cityId } = useParams();
  const [places, setPlaces] = useState([]);

  useEffect(() => {
    const fetchPlaces = async () => {
      const q = query(collection(db, "places"), where("cityId", "==", cityId));
      const snapshot = await getDocs(q);
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setPlaces(data);
    };

    fetchPlaces();
  }, [cityId]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>üó∫Ô∏è ÿßŸÑÿ£ŸÖÿßŸÉŸÜ ÿßŸÑÿ≥Ÿäÿßÿ≠Ÿäÿ© ŸÅŸä Ÿáÿ∞Ÿá ÿßŸÑŸÖÿØŸäŸÜÿ©</h2>
      {places.length === 0 ? (
        <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÖÿßŸÉŸÜ ŸÖÿ≥ÿ¨ŸÑÿ© ÿ®ÿπÿØ.</p>
      ) : (
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "15px"
        }}>
          {places.map(place => (
            <div key={place.id} style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              overflow: "hidden",
              boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
            }}>
              <img src={place.imageUrl} alt={place.name} style={{ width: "100%", height: "200px", objectFit: "cover" }} />
              <div style={{ padding: "10px" }}>
                <h3>{place.name}</h3>
                <p>{place.description}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
  const { user } = useAuth();

const saveToFavorites = async (place) => {
  if (!user) {
    alert("Ÿäÿ¨ÿ® ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÑÿ≠ŸÅÿ∏ ÿßŸÑŸÖŸÉÿßŸÜ");
    return;
  }

  try {
    const favRef = doc(db, "favorites", user.uid, "places", place.id);
    await setDoc(favRef, place);
    alert("‚úÖ ÿ™ŸÖ ÿßŸÑÿ≠ŸÅÿ∏ ŸÅŸä ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©");
  } catch (err) {
    alert("‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ≠ŸÅÿ∏");
    console.error(err);
  }
};

}

export default Places;
