import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";
import { collection, query, where, getDocs, doc, setDoc } from "firebase/firestore";
import { useAuth } from "../AuthContext";

function Places() {
  const { cityId } = useParams();
  const [places, setPlaces] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const { user } = useAuth();

  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        const q = query(collection(db, "places"), where("cityId", "==", cityId));
        const snapshot = await getDocs(q);
        const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setPlaces(data);
      } catch (error) {
        console.error("خطأ أثناء جلب الأماكن:", error);
      }
    };

    fetchPlaces();
  }, [cityId]);

  const saveToFavorites = async (place) => {
    if (!user) {
      alert("يرجى تسجيل الدخول لحفظ في المفضلة.");
      return;
    }

    try {
      const favRef = doc(db, "favorites", user.uid, "places", place.id);
      await setDoc(favRef, place);
      alert("✅ تم الحفظ في المفضلة");
    } catch (err) {
      alert("❌ فشل في الحفظ");
      console.error(err);
    }
  };

  const filteredPlaces = places.filter(place =>
    place.name && place.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
  gap: "25px"
}}>
  {filteredPlaces.map(place => (
    <div key={place.id} style={{
      borderRadius: "15px",
      overflow: "hidden",
      backgroundColor: "#fff",
      boxShadow: "0 6px 16px rgba(0,0,0,0.1)",
      display: "flex",
      flexDirection: "column",
      transition: "transform 0.3s"
    }}>
      <div style={{ width: "100%", height: "230px", overflow: "hidden" }}>
        <img
          src={place.imageUrl || "https://via.placeholder.com/400x200?text=No+Image"}
          alt={place.name || "مكان"}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover"
          }}
        />
      </div>
      <div style={{ padding: "20px", flexGrow: 1 }}>
        <h3 style={{ margin: "0 0 10px", color: "#023e8a" }}>{place.name || "بدون اسم"}</h3>
        <p style={{ color: "#555", minHeight: "50px" }}>
          {place.description || "لا يوجد وصف."}
        </p>
      </div>
      <button
        onClick={() => saveToFavorites(place)}
        style={{
          backgroundColor: "#ff4081",
          color: "white",
          border: "none",
          borderTop: "1px solid #eee",
          padding: "12px",
          fontSize: "15px",
          cursor: "pointer",
          borderRadius: "0 0 15px 15px"
        }}
      >
        ❤️ حفظ في المفضلة
      </button>
    </div>
  ))}
</div>
  );
}

export default Places;
